import React, { useEffect } from 'react';
import { CircularProgress } from '@material-ui/core';
import Modal from '@material-ui/core/Modal';
import { makeStyles } from '@material-ui/core/styles';
import { useSelector, useDispatch } from 'react-redux';
import { index, photo } from '../../store/actions/items.action';
import { add_items, remove_items, get_cart } from '../../store/actions/cart.action';
import { style } from './style.css';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: '80%',
    height: '60%',
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    top: '20%',
    left: '10%'
  },
}));

export default function BannerItems(){

  const dispatch = useDispatch();
  const items = useSelector(state => state.Items);

  function indexCall() {
    dispatch( index() );
  }

  //Abertura de itens

  const classes = useStyles();
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [open, setOpen] = React.useState(false);
  const [item, setItem] = React.useState({ 
    id: null,
    foto: null, 
    desc: null,
    preco: null
  });
  const [itemQuantity, setItemQuanity] = React.useState(1);

  //input de tamanho
  const [value, setValue] = React.useState('');

  const handleChange = (event) => {
    setValue(event.target.value);
  };
  //!imput de tamanho

  const handleOpen = (id, foto, desc, preco, tipo) => {
    setOpen(true);
    setItem({ id: id,
              foto: foto, 
              desc: desc,
              preco: preco,
              tipo: tipo
            });
  };

  const handleClose = () => {
    setOpen(false);
  };

  //!Abertura de itens

  useEffect (() => {
    if (!items.LOADING){
      for(let i = 0; i < items.ITEMS.length; i = i + 1 ) {
        dispatch( photo( items.ITEMS[i].id ) )
      }
    }
  }, [items.LOADING]);

  useEffect( () => {
    indexCall();
  }, []);


  return (
    <div className="container pt-5">
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        <div className={'row d-flex align-items-center '.concat(classes.paper)}>
          <div className='col-md-6 d-flex align-items-center' height='100%'>
            <img src={item.foto} height='100%'></img>
          </div>
          <div className='col-md-6 d-flex flex-column align-items-center justify-content-around item-display'>
            <h2>
              {item.desc}
            </h2>
            <div>
              {item.tipo !== 3 && <>
                {setValue('p')}
                <FormControl component="fieldset">
                  <FormLabel component="legend">TAMANHO</FormLabel>
                  <RadioGroup aria-label="tamanho" name="tamanho" value={value} onChange={handleChange}>
                    <FormControlLabel value="p" control={<Radio />} label="P" />
                    <FormControlLabel value="m" control={<Radio />} label="M" />
                    <FormControlLabel value="g" control={<Radio />} label="G" />
                  </RadioGroup>
                </FormControl>
              </>}
              {item.tipo === 3 && <>
                {setValue('37')}
                <FormControl className={classes.formControl}>
                  <InputLabel id="demo-simple-select-label">TAMANHO</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={value}
                    onChange={handleChange}
                  >
                    <MenuItem value={37}>37</MenuItem>
                    <MenuItem value={38}>38</MenuItem>
                    <MenuItem value={39}>39</MenuItem>
                    <MenuItem value={40}>40</MenuItem>
                    <MenuItem value={41}>41</MenuItem>
                    <MenuItem value={42}>42</MenuItem>
                  </Select>
                </FormControl>
              </>}
            </div>
            <div>
              <h3>
                R$ {item.preco}
              </h3>
            </div>
            <button 
            onClick={()=>{
              dispatch( add_items(item.id, 1, item.foto, item.desc, item.preco, item.tamanho));
            }}
            >
              Adicionar ao carrinho
            </button>
          </div>
        </div>
      </Modal>
      <div className="row justify-content-center">
        <div className="col-auto">
          <h1>
            Ãšltimas novidades
          </h1>
        </div>
      </div>
      <div className="row justify-content-center">
        {items.LOADING ? <CircularProgress />
        :
        <>
          {items.ITEMS.map( (item) =>
            <div
              key={item.id}
              className='col-md-4 justify-content-center d-flex card'
            >
              <div className='row d-flex flex-row justify-content-center'>
                {items.LOADING_PHOTO ? <CircularProgress />
                :
                  <>{items.PHOTO.map( (photo) => <>{photo.id !== item.id ? ''
                    :
                    <img
                    key={photo.id}
                    className='p-2 foto'
                    src={'http://api-lojabim.test/'.concat([photo.img])}
                    onClick={(e) => handleOpen(item.id, 'http://api-lojabim.test/'.concat([photo.img]), item.desc, item.preco, item.tipo)}
                    ></img>
                  }</>)}</>
                }
              </div>
              <div className='row d-flex flex-row desc'>
                <div className="pl-5">
                  {item.desc}
                </div>
              </div>
              <div className='row d-flex flex-row preco'>
                <div className="pl-5">
                  R${item.preco}
                </div>
              </div>
            </div>
          )}
        </>
        }
      </div>
    </div>
  )
}
