import { useState, useEffect } from 'react';
import Modal from '@material-ui/core/Modal';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import Select from '@material-ui/core/Select';
import { makeStyles } from '@material-ui/core/styles';
import styled from 'styled-components';
import InputLabel from '@material-ui/core/InputLabel';

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: '80%',
    height: '60%',
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    top: '20%',
    left: '10%'
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  }
}));

const Categories = styled.div`
  min-height: 6vh;
  background-color: #474a51;
  color: white;
`;

export default function OpenedItems(props) {

  const dispatch = useDispatch();

  //input de tamanho e retorno
  const [tamanho, setTamanho] = useState(undefined);

  const handleChange = (event) => {
    setTamanho(event.target.value);
  };

  //Abertura de itens

  const classes = useStyles();

  function tamanhoSel(tipo){

    //Tipos de vestimenta:
    //  1 - Camisa Slim (P ao XG)
    //  2 - Camisa Street (M ao XXG)
    //  3 - Chinelo (37 ao 43)
    //  4 - Bermudas e calças (P ao G)
    if (tipo !== 3){
      return (<>
        <FormControl component="fieldset">
          <FormLabel component="legend">TAMANHO</FormLabel>
          <RadioGroup aria-label="tamanho" name="tamanho" value={tamanho} onChange={handleChange}>
            
          { tipo === 1 && <>
              <FormControlLabel value="p" control={<Radio />} label="P" />
            </>}
            <FormControlLabel value="m" control={<Radio />} label="M" />
            <FormControlLabel value="g" control={<Radio />} label="G" />
            { tipo === 1 && <>
              <FormControlLabel value="xg" control={<Radio />} label="XG" />
            </>}
            { tipo === 2 && <>
              <FormControlLabel value="xxg" control={<Radio />} label="XXG" />
            </>}
          </RadioGroup>
        </FormControl>
      </>);
    } else {
      return (<>
        <FormControl className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">TAMANHO</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={tamanho}
            onChange={handleChange}
          >
            <MenuItem value={37}>37</MenuItem>
            <MenuItem value={38}>38</MenuItem>
            <MenuItem value={39}>39</MenuItem>
            <MenuItem value={40}>40</MenuItem>
            <MenuItem value={41}>41</MenuItem>
            <MenuItem value={42}>42</MenuItem>
            <MenuItem value={43}>43</MenuItem>
          </Select>
        </FormControl>
      </>);
    }
  }

  const handleOpen = (id, desc, preco, tipo) => {
    setOpen(true);
    setItem({ id: id,
              desc: desc,
              preco: preco,
              tipo: tipo
            });
  };

  const handleClose = () => {
    setOpen(false);
  };

  useEffect(()=>{
    dispatch( index(tipoItens) );
  },[tipoItens]);

  // getModalStyle is not a pure function, we roll the style only on the first render
  const [open, setOpen] = useState(false);
  const [item, setItem] = useState({ 
    id: null,
    foto: null, 
    desc: null,
    preco: null
  });

  return(
    <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
      <div className={'row d-flex align-items-center '.concat(classes.paper)}>
        <div className='col-md-6 d-flex align-items-center' height='100%'>
          <ShowPhotos id={item.id}/>
        </div>
        <div className='col-md-6 d-flex flex-column align-items-center justify-content-around item-display'>
          <h2>
            {item.desc}
          </h2>
          <div>
            {tamanhoSel(item.tipo)}
          </div>
          <div>
            <h3>
              R$ {item.preco}
            </h3>
          </div>
          <button
          onClick={()=>{
            if (tamanho === undefined){
              dispatch( changeNotify({
                open: true,
                class: 'error',
                msg: 'Selecione um tamanho.'
              }));
            } else {
              dispatch( add_items(item.id, 1, item.foto, item.desc, item.preco, tamanho));
              setTamanho(undefined);
              handleClose();
            }
          }}
          >
            Adicionar ao carrinho
          </button>
        </div>
      </div>
    </Modal>
  );
}
