import React, { useEffect } from 'react';
import { CircularProgress } from '@material-ui/core';
import Modal from '@material-ui/core/Modal';
import { makeStyles } from '@material-ui/core/styles';
import { useSelector, useDispatch } from 'react-redux';
import { index, photo } from '../../store/actions/items.action';
import { add_items, remove_items, get_cart } from '../../store/actions/cart.action';
import { style } from './style.css';

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function BannerItems(){

  const dispatch = useDispatch();
  const items = useSelector(state => state.Items);

  function indexCall() {
    dispatch( index() );
  }

  //Abertura de itens

  const classes = useStyles();
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);
  const [item, setItem] = React.useState(undefined);

  const handleOpen = (id, foto, desc, preco) => {
    setOpen(true);
    setItem([id, foto, desc,preco]);
  };

  const handleClose = () => {
    setOpen(false);
  };

  //!Abertura de itens

  useEffect (() => {
    if (!items.LOADING){
      for(let i = 0; i < items.ITEMS.length; i = i + 1 ) {
        dispatch( photo( items.ITEMS[i].id ) )
      }
    }
  }, [items.LOADING]);

  useEffect( () => {
    indexCall();
  }, []);


  return (
    <div className="container pt-5">
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        <div style={modalStyle} className={classes.paper}>
          {item[1]}
        </div>
      </Modal>
      <div className="row justify-content-center">
        <div className="col-auto">
          <h1>
            Ãšltimas novidades
          </h1>
        </div>
      </div>
      <div className="row justify-content-center">
        {items.LOADING ? <CircularProgress />
        :
        <>
          {items.ITEMS.map( (item) =>
            <div
              key={item.id}
              className='col-md-4 justify-content-center d-flex card'
            >
              <div className='row d-flex flex-row justify-content-center'>
                {items.LOADING_PHOTO ? <CircularProgress />
                :
                  <>{items.PHOTO.map( (photo) => <>{photo.id !== item.id ? ''
                    :
                    <img
                    key={photo.id}
                    className='p-2 foto'
                    src={'http://api-lojabim.test/'.concat([photo.img])}
                    /*onClick={()=>{
                      dispatch( add_items(item.id, 1, 'http://api-lojabim.test/'.concat([photo.img]), item.desc, item.preco));
                    }}*/
                    onClick={(e) => handleOpen(item.id, 'http://api-lojabim.test/'.concat([photo.img]), item.desc, item.preco)}
                    ></img>
                  }</>)}</>
                }
              </div>
              <div className='row d-flex flex-row desc'>
                <div className="pl-5">
                  {item.desc}
                </div>
              </div>
              <div className='row d-flex flex-row preco'>
                <div className="pl-5">
                  R${item.preco}
                </div>
              </div>
            </div>
          )}
        </>
        }
      </div>
    </div>
  )
}
