import React, { useEffect } from 'react';
import { CircularProgress } from '@material-ui/core';
import { useSelector, useDispatch } from 'react-redux';
import { index, photo } from '../../store/actions/items.action';
import { add_items, remove_items, get_cart } from '../../store/actions/cart.action';
import { style } from './style.css';

export default function BannerItems(){

  const dispatch = useDispatch();
  const items = useSelector(state => state.Items);

  function indexCall() {
    dispatch( index() );
  }

  function openItems() {
    
  }

  useEffect (() => {
    if (!items.LOADING){
      for(let i = 0; i < items.ITEMS.length; i = i + 1 ) {
        dispatch( photo( items.ITEMS[i].id ) )
      }
    }
  }, [items.LOADING]);

  useEffect( () => {
    indexCall();
  }, []);


  return (
    <div className="container pt-5">
      <Modal
        disablePortal
        disableEnforceFocus
        disableAutoFocus
        open
        aria-labelledby="server-modal-title"
        aria-describedby="server-modal-description"
        className={classes.modal}
        container={() => rootRef.current}
      >
        <div className={classes.paper}>
          <h2 id="server-modal-title">Server-side modal</h2>
          <p id="server-modal-description">If you disable JavaScript, you will still see me.</p>
        </div>
      </Modal>
      <div className="row justify-content-center">
        <div className="col-auto">
          <h1>
            Ãšltimas novidades
          </h1>
        </div>
      </div>
      <div className="row justify-content-center">
        {items.LOADING ? <CircularProgress />
        :
        <>
          {items.ITEMS.map( (item) =>
            <div
              key={item.id}
              className='col-md-4 justify-content-center d-flex card'
            >
              <div className='row d-flex flex-row justify-content-center'>
                {items.LOADING_PHOTO ? <CircularProgress />
                :
                  <>{items.PHOTO.map( (photo) => <>{photo.id !== item.id ? ''
                    :
                    <img
                    key={photo.id}
                    className='p-2 foto'
                    src={'http://api-lojabim.test/'.concat([photo.img])}
                    /*onClick={()=>{
                      dispatch( add_items(item.id, 1, 'http://api-lojabim.test/'.concat([photo.img]), item.desc, item.preco));
                    }}*/
                    ></img>
                  }</>)}</>
                }
              </div>
              <div className='row d-flex flex-row desc'>
                <div className="pl-5">
                  {item.desc}
                </div>
              </div>
              <div className='row d-flex flex-row preco'>
                <div className="pl-5">
                  R${item.preco}
                </div>
              </div>
            </div>
          )}
        </>
        }
      </div>
    </div>
  )
}
