import React, { useState, useEffect } from 'react';
import { CircularProgress } from '@material-ui/core';
import { useSelector, useDispatch } from 'react-redux';
import { photo } from '../../store/actions/items.action';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { get_cart, set_active } from '../../store/actions/cart.action';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import { style } from './style.css'; 
import { remove_items, add_items } from '../../store/actions/cart.action';
import { HttpAuth } from '../../config/Http';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function Cart(props){

  const [cartButtonOff, setCartButtonOff] = useState(false);

  const [open, setOpen] = useState(false);

  //L贸gica de abrtura e fechamento do popup carrinho
  function handleOpen(){
    return (true);
  }

  function handleClose(){
    return true;
  }
  //L贸gica de abrtura e fechamento do popup carrinho

  //TOTAL DO CARRINHO
  let total = 0;
  const adicionarTotal = (preco, qtd) => {
    total = total + (parseInt(preco) * parseInt(qtd))
  }
  //!TOTAL DO CARRINHO

  //CART REDUCER
  const cart = useSelector(state => state.Cart);
  const dispatch = useDispatch();

  const indexCall = ()=>{
    dispatch( get_cart() );
  };

  useEffect(()=>{
    indexCall();
  },[]);
  //!CART REDUCER

  const classes = useStyles();

  //Payment call (pagseguro)
  function payment(){

    setCartButtonOff(true);

    let cart = localStorage.getItem('storeCart');

    if ((typeof cart !== 'undefined' || cart !== '')){
      
      let itemsIdQtd = [];

      //LOGICA DE ADICINAR OS IDS DO CARRINHO
      for (let i = 0; i < JSON.parse(cart).length; i++){
  
        itemsIdQtd = [...itemsIdQtd, {
          itemId: JSON.parse(cart)[i].id, itemQuantity: JSON.parse(cart)[i].qtd
        }];
  
      }

      localStorage.removeItem('storeCart');
  
      HttpAuth.post('checkout', {items: JSON.stringify(itemsIdQtd)}).then(
        (response) => {
          window.location = response.data;
          ;
        }
      );
    }
  }

  return (
    <div>
      
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={handleOpen()}
        onClose={handleClose()}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={handleClose()}>
          <div className={classes.paper}>
            <div className='container-fluid'>
              <div classame='row'>
                <h2 
                  className='cartTitle col-12 d-flex justify-content-center' id="transition-modal-title">
                  Carrinho
                </h2>
              </div>
              <div clasName='row'>
                {cart.ITEMS.map( (item) => <div key={item.id}>
                  <div class='row'>
                    <div className='col-4'>
                      <img width='50%' src={item.img}/>
                    </div>
                    <div className='col-8'>
                      <div className='namePrice-container'>
                        <div className='name-container'>
                          <h3>
                            {item.nome}
                          </h3>
                        </div>
                        <div className='priceQtd-container'>
                          <div>
                            <span>
                              <AddIcon
                              className='addButton'
                              onClick={ (e) => dispatch( add_items(item.id) ) }
                              />
                              {item.qtd}
                              <RemoveIcon
                                className='removeButton'
                                onClick={ (e) => dispatch( remove_items(item.id) ) }
                              />
                            </span>
                          </div>
                          <span className='itemPreco'>R$ {item.preco}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                  {adicionarTotal(item.preco, item.qtd)}
                  <hr />
                </div>)}
              </div>
            </div>
            <div className='row'>
              <span class='col-4 d-flex justify-content-center'>
                <h4>
                  Total
                </h4>
              </span>
              <span class='col-4 d-flex justify-content-center'>
                <h4>
                  R${total}
                </h4>
              </span>
              <span class='col-4 d-flex justify-content-center'>
                <button
                onClick={ (e) => {payment()} }
                disabled = { cartButtonOff && true}
                >
                  Finalizar compra
                </button>
              </span>
            </div>
          </div>
        </Fade>
      </Modal>
    </div>
  );
}
